import { useNavigate, Link } from "react-router-dom";
import { useState } from "react";
import { addcat } from "../Services/cat";
import Sidebar from "../Component/sidebar";

function AddCat() {
  const [title, setCategory] = useState("");
  const [description, setDescription] = useState("");

  const navigate = useNavigate();

  const onCreate = async () => {
    if (title.length === 0) {
      alert("Title email");
    } else if (description.length === 0) {
      alert("enter Contents");
    } else {
      const result = await addcat(title, description);
      if (result["status"] === "success") {
        alert("successfully add Blog");
        navigate("/home");
      } else {
        alert("Failed to add Blog");
      }
    }
  };

  return (
    <div>
      <Sidebar />
      <div className="row ">
        <div className="col"></div>
        <div className="col whole">
          <h1 className="title ">Create Category</h1>
          <div className="form">
            <div className="mb-2">
              {" "}
              <h4>Catergory</h4>
              <input
                type="text"
                placeholder="Enter Catergory"
                className="form-control"
                onChange={(e) => {
                  setCategory(e.target.value);
                }}
              />
            </div>
            <div className="mb-2">
              {" "}
              <h4>Description</h4>
              <input
                type="textarea"
                placeholder="Enter Description"
                className="form-control"
                onChange={(e) => {
                  setDescription(e.target.value);
                }}
              />
            </div>

            <div className="bottoncentre">
              <botton onClick={onCreate} className="btn btn-success">
                Add Category
              </botton>
              <botton className="btn btn-success">
                <Link to="/home">Cancel</Link>
              </botton>
            </div>
          </div>
        </div>
        <div className="col"></div>
      </div>
    </div>
  );
}

export default AddCat;
