import { useNavigate, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { addblog } from "../Services/blog";
import { getcat } from "../Services/cat";
import Sidebar from "../Component/sidebar";

function CreateBlog() {
  const [Title, setTitle] = useState("");
  const [Contents, setContents] = useState("");
  const [dropdown, setdropdown] = useState("");
  const [dropdown1, setdropdown1] = useState([]);
  // const [key, setKey] = useState("");

  const navigate = useNavigate();

  const onCreate = async () => {
    if (Title.length === 0) {
      alert("Title ");
    } else if (Contents.length === 0) {
      alert("enter Contents");
    } else if (dropdown.length === 0) {
      alert("enter dropdown");
    } else if (dropdown.length >0) {
        setdropdown(Number(dropdown.value));
    } else {
      const result = await addblog(Title, Contents, setdropdown);
      if (result["status"] === "success") {
        alert("successfully add Blog");
        navigate("/home");
      } else {
        alert("Failed to add Blog");
      }
    }
  };

  const LoadCategories = async () => {
    const result = await getcat();
    if (result["status"] === "success") {

      setdropdown1(result["data"]);
      alert("Data foound ");
    } else {
      alert("Data Not found ");
    }
  };
  // used to perform action(s) after component gets loaded
  useEffect(() => {
    LoadCategories();
  }, []);

  return (
    <div>
      <Sidebar />
      <div className="row ">
        <div className="col"></div>
        <div className="col whole">
          <h1 className="title ">Create Blog</h1>
          <div className="form">
            <div className="mb-2">
              {" "}
              <h4>Title</h4>
              <input
                type="text"
                placeholder="Enter Title"
                className="form-control"
                onChange={(e) => {
                  setTitle(e.target.value);
                }}
              />
            </div>
            <div className="mb-2">
              {" "}
              <h4>Contents</h4>
              <input
                type="textarea"
                placeholder="Enter Contents"
                className="form-control"
                onChange={(e) => {
                  setContents(e.target.value);
                }}
              />
            </div>
            <h4>Select Category</h4>
            <select
              value="dropdown"
              onChange={(e) => setdropdown(e.target.value)}
            >
              {dropdown1.map((cat) => {
                return (
                  <option key={cat.id} value={cat.id}>
                    {cat.title}
                  </option>
                );
              })}
            </select>
            <div className="bottoncentre">
              <botton onClick={onCreate} className="btn btn-success">
                Create
              </botton>
              <botton className="btn btn-success">
                <Link to="/home">Cancel</Link>
              </botton>
            </div>
          </div>
        </div>
        <div className="col"></div>
      </div>
    </div>
  );
}

export default CreateBlog;
