import { useState } from "react";
// import { toast } from "react-toastify";
import Sidebar from "../Component/sidebar";
import { findblogs } from "../Services/blog";

function SerachBlog() {
  const [keyword, setKeyword] = useState("");

  const onsearch = async () => {
    if (keyword.length === 0) {
      alert("enter keyword");
    } else {
      const result = await findblogs(keyword);
      if (result["status"] === "success") {
        alert("welcome to the application");
      } else {
        alert("not found");
      }
    }
  };

  return (
    <div>
      <Sidebar />
      <h2 className="page-title">Search Blog</h2>
      <div className="row">
        <div className="col"></div>
        <div className="col">
          <div className="form">
            <div className="mb-3">
              <label htmlFor="">Keyword</label>
              <input
                onChange={(e) => {
                  setKeyword(e.target.value);
                }}
                type="text"
                className="form-control"
              />
            </div>
            <button onClick={onsearch} className="mt-2 btn btn-success">
              Search
            </button>
          </div>
        </div>
      </div>
      <div className="col"></div>
    </div>
  );
}

export default SerachBlog;
