import config from "../config";
import axios from "axios";

export async function allCat() {
  const token = sessionStorage.getItem("token");

  const response = await axios.get(`${config.url}/cat/displaycategory`, {
    headers: {
      token,
    },
  });
  return response.data;
}

export async function addcat(title, description) {
  const token = sessionStorage.getItem("token");

  const body = { title, description };
  const response = await axios.post(`${config.url}/cat/addcategory`, body, {
    headers: {
      token,
    },
  });
  return response.data;
}

export async function getcat() {
  const token = sessionStorage.getItem("token");

  const response = await axios.get(`${config.url}/cat/getcat`, {
    headers: {
      token,
    },
  });
  return response.data; // Assuming your backend sends back a JSON response with a "status" field
}
