import { useEffect, useState } from "react";
// import { login } from "../Services/user";
import { useParams } from "react-router-dom";
import { getblogs } from "../Services/blog";
// import { toast } from "react-toastify";
import Sidebar from "../Component/sidebar";

function BlogDetails() {
  const { id } = useParams();
  const [details, setDetails] = useState(undefined);
  const [amenities, setAmenities] = useState();

  const Loadblogs = async () => {
    const result = await getblogs(id);
    if (result["status"] === "success") {
    } else {
      alert.error(result["error"]);
    }

    setAmenities(amenities);
    setDetails(result["data"]);
    console.log(result["data"]);
  };

  useEffect(() => {
    Loadblogs();
  });
  return (
    <div>
      <Sidebar />
      <h2 className="page-title">Blog Details</h2>
      {details && (
        <div className="mt-5">
          <h3 style={{ fontSize: 26 }}>{details.title}</h3>
          <hr />

          <div className="mt-3">
            <div>
              Hosted by:{" "}
              <span style={{ fontWeight: "bold" }}>{details.contents}</span>
            </div>
            <div>{details.contactNo}</div>
            <hr />
          </div>
          <div className="mt-3">
            <h4>About the place</h4>
            <div>{details.category}</div>
            <hr />
          </div>
        </div>
      )}
    </div>
  );
}

export default BlogDetails;
