// user.js

import axios from 'axios';
import config from '../config';

export async function verifyEmail(email) {
  try {
    const response = await axios.post(`${config.url}/user/verify-email`, { email });
    return response.data;
  } catch (error) {
    throw new Error(error.response.data.message || 'Error verifying email');
  }
}

export async function getPassword(email, verificationCode) {
  try {
    const response = await axios.post(`${config.url}/user/get-password`, { email, verificationCode });
    return response.data;
  } catch (error) {
    throw new Error(error.response.data.message || 'Error retrieving password');
  }
}

// Other functions like login and register
export async function login(email, password) {
  const body = { email, password };
  const response = await axios.post(`${config.url}/user/login`, body);
  return response.data;
}

export async function register(fullName, email, password, phoneNO) {
  const body = { fullName, email, password, phoneNO};
  const response = await axios.post(`${config.url}/user/register`, body);
  return response.data; // Assuming your backend sends back a JSON response with a "status" field
}



