import config from "../config";
import axios from "axios";

export async function addblog(title, contents, categoryId) {
  const token = sessionStorage.getItem("token");

  const body = { title, contents, categoryId };
  const response = await axios.post(`${config.url}/blog/createblog`, body, {
    headers: {
      token,
    },
  });
  return response.data;
}

export async function getblogs(id) {
  const token = sessionStorage.getItem("token");

  const body = { id };
  const response = await axios.get(`${config.url}/blog/get/${id}`, body, {
    headers: {
      token,
    },
  });
  return response.data; // Assuming your backend sends back a JSON response with a "status" field
}

export async function verfiytoken(id) {}

export async function findblogs(id) {
  const body = { id };
  const token = sessionStorage.getItem("token");

  const response = await axios.post(`${config.url}/blog/search`, body, {
    headers: {
      token,
    },
  });
  return response.data; // Assuming your backend sends back a JSON response with a "status" field
}
